mapper.insert:
insert into t_check_order (id, user_id, product_id,
  order_id, status, finish_desc, 
  audit_desc, create_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, 
  #{orderId,jdbcType=INTEGER}, #{status,jdbcType=VARCHAR}, #{finishDesc,jdbcType=VARCHAR}, 
  #{auditDesc,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select t1.id, t1.user_id, t1.product_id, t1.order_id, t1.status, t1.finish_desc, t1.audit_desc, t1.create_time, t1.update_time, t1.amount, t1.auditor, t2.real_name, t2.phone from t_check_order t1 left join t_user t2 on t1.user_id = t2.id where 1 = 1
and t1.status != '1'
and t1.status = #{status,jdbcType=VARCHAR}
and t1.order_id = #{orderId,jdbcType=INTEGER}

SELECT t1.id , t1.user_id , t1.order_id, t1.product_id , t1.status , t1.finish_desc, t1.audit_desc, t1.amount, t1.auditor, t1.create_time , t1.update_time , t2.name product_name, t2.type , t2.attr , t2.budget , t2.status product_status, t2.expect_delivery_time, t2.name product_name, t3.phone, t3.real_name FROM t_check_order t1 LEFT JOIN t_product t2 ON t1.product_id = t2.id left join t_user t3 on t1.user_id = t3.id where 1=1
and t2.name like concat('%',#{productName},'%')
and t1.product_id = #{productId,jdbcType=INTEGER}
and t2.type = #{type}
and t2.attr = #{attr}
and t1.status in ('1','2','3','4')
and t1.status in ('2','4')
and t1.status = #{status}
